           #{cate_cd}
       , #{title}
       , #{content}
       , #{tag}
       , 0
       , #{reg_id}
       , now()
       , now()
       )

mapper.update:
UPDATE tbl_board SET
    CATE_CD = #{cate_cd}
                   , TITLE = #{title}
                   , CONTENT = #{content}
                   , TAG = #{tag}
                   , EDIT_DT = now()
WHERE
    BID = ${bid}
